const {
  Model,
} = require('sequelize');

module.exports = (sequelize, DataTypes) => {
  class Recipe extends Model {
    static associate({
      User, Comment, Kitchen, Step, Recipe_product, Favorite_recipe,
    }) {
      Recipe.belongsTo(User, { foreignKey: 'user_id' });
      Recipe.hasMany(Comment, { foreignKey: 'recipe_id' });
      Recipe.belongsTo(Kitchen, { foreignKey: 'kitchen_id' });
      Recipe.hasMany(Step, { foreignKey: 'step_id' });
      Recipe.hasMany(Recipe_product, { foreignKey: 'recipe_id' });
      Recipe.hasMany(Favorite_recipe, { foreignKey: 'recipe_id' });
    }
  }
  Recipe.init({
    title: {
      type: DataTypes.TEXT,
      allowNull: false,
    },
    body: {
      type: DataTypes.TEXT,
      allowNull: false,
    },
    kitchen_id: {
      type: DataTypes.INTEGER,
      allowNull: false,
      references: {
        model: 'Kitchens',
        key: 'id',
      },
      defaultValue: 1,
    },
    user_id: {
      type: DataTypes.INTEGER,
      allowNull: false,
      references: {
        model: 'Users',
        key: 'id',
      },
    },
    img: {
      type: DataTypes.TEXT,
      allowNull: false,
      defaultValue: 'data:image/png;base64,',
    },
    delete_visible: {
      type: DataTypes.BOOLEAN,
      allowNull: false,
      defaultValue: false,
    },
    moder_visible: {
      type: DataTypes.BOOLEAN,
      allowNull: false,
      defaultValue: false,
    },
    private: {
      type: DataTypes.BOOLEAN,
      allowNull: false,
      defaultValue: false,
    },
  }, {
    sequelize,
    modelName: 'Recipe',
    tableName: 'Recipes',
  });
  return Recipe;
};
